import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FinanceService } from './services/finance.service';
import {
  Chart,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  LineController,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

Chart.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  LineController, // Ensure the LineController is registered
  Title,
  Tooltip,
  Legend
);


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  @ViewChild('stockChart') stockChart!: ElementRef;
  chart!: Chart;
  title = 'finance-dashboard';
  stockData: any;
  topStocks: any; // Adjust if you have a specific type defined
  error: string | null = null;
  ticker: string = '';

  constructor(private financeService: FinanceService) { }

  ngOnInit() {
    this.fetchTopStocks();
  }

  initChart() {
    if (!this.topStocks || !this.stockChart) {
      console.warn('Required data or element is not available.');
      return;
    }

    this.chart = new Chart(this.stockChart.nativeElement, {
      type: 'line', // Make sure 'line' matches the registered controller
      data: {
        labels: Object.keys(this.topStocks),
        datasets: [{
          label: 'Stock Price',
          data: Object.values(this.topStocks).map(stock => this.getLastPrice(stock)),
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }

  changeChart(type: string) {
    this.initChart(type); // Change chart type
  }

  fetchTopStocks() {
    this.financeService.getTopStocks().subscribe({
      next: (data) => {
        this.topStocks = data;
        if (this.topStocks) {
          this.initChart();  // Initialize the chart after the data is available
        }
      },
      error: () => {
        this.error = 'Failed to fetch top stocks';
      }
    });
  }

  getLastPrice(prices: any): number {
    const lastDate = Object.keys(prices).pop(); // gets the last key
    return prices[lastDate!]; // returns the price associated with the last date
  }

  searchTicker(ticker: string) {
    this.financeService.getStockPrice(ticker).subscribe({
      next: (data) => {
        this.stockData = data;
        this.error = null;
      },
      error: (error) => {
        this.error = "Failed to fetch data";
        this.stockData = null;
      }
    });
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Finance Dashboard</title>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bulma/0.9.3/css/bulma.min.css">
  <script defer src="https://use.fontawesome.com/releases/v5.14.0/js/all.js"></script> <!-- for icons -->
</head>

<body>
  <section class="hero is-dark is-bold">
    <div class="hero-body">
      <div class="container has-text-centered">
        <h1 class="title">Finance Dashboard</h1>
        <h2 class="subtitle">Monitor key financial metrics</h2>
        <button class="button is-light" id="toggleTheme">Toggle Theme</button>
      </div>
    </div>
  </section>

  <div class="container mt-5">
    <div class="columns">
      <div class="column is-one-quarter">
        <aside class="menu">
          <p class="menu-label has-text-white">General</p>
          <ul class="menu-list">
            <li><a>Dashboard</a></li>
            <li><a>Stocks</a></li>
          </ul>
          <p class="menu-label has-text-white">Administration</p>
          <ul class="menu-list">
            <li><a>Team Settings</a></li>
          </ul>
        </aside>
      </div>
      <div class="column is-three-quarters">
        <div class="box has-background-grey-dark">
          <h3 class="title is-4 has-text-white">Stock Search</h3>
          <div class="field has-addons">
            <div class="control is-expanded">
              <input class="input" type="text" [(ngModel)]="ticker" placeholder="Enter Ticker (e.g., AAPL)">
            </div>
            <div class="control">
              <button class="button is-info">Search</button>
            </div>
          </div>
          <div *ngIf="stockData" class="notification is-primary">
            <h2 class="subtitle">Stock Price: {{ stockData.closing_price | number:'1.2-2' }}</h2>
          </div>
          <div *ngIf="error" class="notification is-danger">
            <h2 class="subtitle">{{ error }}</h2>
          </div>
          <div class="tabs is-toggle is-fullwidth">
            <ul>
              <li class="is-active" (click)="changeChart('line')"><a>Line</a></li>
              <li (click)="changeChart('bar')"><a>Bar</a></li>
              <li (click)="changeChart('radar')"><a>Radar</a></li>
              <li (click)="changeChart('pie')"><a>Pie</a></li>
            </ul>
          </div>
          <canvas #stockChart width="800" height="400"></canvas>
        </div>
      </div>
    </div>
  </div>

  <script>
    document.getElementById('toggleTheme').addEventListener('click', function() {
      document.body.classList.toggle('is-light');
    });
  </script>
</body>

</html>
