import { Component } from '@angular/core';
import { FinanceService, StockData } from './services/finance.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'finance-dashboard';
  stockData: any;
  topStocks: StockData | null = null;
  error: string | null = null;
  ticker: string = '';

  constructor(private financeService: FinanceService) { }

  ngOnInit() {
    this.fetchTopStocks();
  }

  fetchTopStocks() {
    this.financeService.getTopStocks().subscribe({
      next: (data) => {
        this.topStocks = data;
      },
      error: () => {
        this.error = 'Failed to fetch top stocks';
      }
    });
  }

  searchTicker(ticker: string) {
    this.financeService.getStockPrice(ticker).subscribe({
      next: (data) => {
        this.stockData = data;
        this.error = null;
      },
      error: (error) => {
        this.error = "Failed to fetch data";
        this.stockData = null;
      }
    });
  }
}
