import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FinanceService } from './services/finance.service';
import {
  Chart,
  CategoryScale,
  ChartConfiguration,
  LinearScale,
  PointElement,
  LineElement,
  LineController,
  Title,
  Tooltip,
  Legend,
  ChartType,
  ChartData,
  ChartOptions,
  ChartTypeRegistry
} from 'chart.js';

Chart.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  LineController,
  Title,
  Tooltip,
  Legend
);

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  @ViewChild('stockChart') stockChart!: ElementRef<HTMLCanvasElement>;
  chart!: Chart;
  error: string | null = null;
  ticker: string = '';
  stockData: StockData | null = null;  // Use the interface and initialize as null
  revenueTrends: any;
  earningsInsights: any;
  marketNews: any;

  constructor(private financeService: FinanceService) { }

  ngOnInit() {
    this.fetchHistoricalTopStocks();
    this.fetchMarketNews();
    this.fetchRevenueTrends('AAPL');
    this.fetchEarningsInsights('AAPL');
  }

  changeChart(type: ChartType) {
    if (!this.stockData) {
      return; // Guard clause if there's no data
    }
    // Assume `stockData` holds data necessary for all chart types
    this.initChart(type, {
      labels: Object.keys(this.stockData), // Adjust accordingly
      datasets: [{
        data: Object.values(this.stockData), // Adjust accordingly
        label: "Stock Performance",
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }]
    });
  }

  initChart(type: keyof ChartTypeRegistry, data: ChartConfiguration['data']) {
    if (this.chart) {
      this.chart.destroy();
    }

    const context = this.stockChart.nativeElement.getContext('2d');
    if (!context) {
      console.error('Failed to get canvas context');
      return;
    }

    const config: ChartConfiguration = {
      type: type as keyof ChartTypeRegistry,
      data: data,
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: false
          }
        }
      }
    };

    this.chart = new Chart(context, config);
  }

  fetchHistoricalTopStocks() {
    this.financeService.getHistoricalTopStocks().subscribe({
      next: (response) => {
        const chartData = {
          labels: response.dates,
          datasets: Object.keys(response.data).map(stock => ({
            label: stock,
            data: response.data[stock],
            borderColor: this.getRandomColor(),
            fill: false,
            tension: 0.1
          }))
        };
        this.initChart('line', chartData);  // Specify 'line' directly here
      },
      error: (err) => {
        this.error = 'Failed to fetch historical stock data';
        console.error(err);
      }
    });
  }

  getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  searchTicker(ticker: string) {
    this.financeService.getStockPrice(ticker).subscribe({
      next: (data) => {
        console.log('Stock Data:', data); // Handle display of individual stock data
        this.error = null;
      },
      error: (err) => {
        this.error = "Failed to fetch data for ticker: " + ticker;
        console.error(err);
        this.stockData = null;
      }
    });
  }

  fetchMarketNews() {
    this.financeService.getMarketNews().subscribe({
      next: (news) => this.marketNews = news,
      error: (err) => this.handleError(err)
    });
  }

  fetchRevenueTrends(ticker: string) {
    if (!ticker) {
      console.error("Ticker is required");
      return;
    }
    this.financeService.getRevenueTrends(ticker).subscribe({
      next: (data) => this.revenueTrends = data,
      error: (err) => this.handleError(err)
    });
  }

  // Call this method with a valid ticker
  fetchEarningsInsights(ticker: string) {
    if (!ticker) {
      console.error("Ticker is required");
      return;
    }
    this.financeService.getEarningsInsights(ticker).subscribe({
      next: (data) => this.earningsInsights = data,
      error: (err) => this.handleError(err)
    });
  }

  handleError(error: any) {
    console.error('An error occurred:', error);
    this.error = 'Failed to load data. Please try again later.';
  }

  toggleTheme(): void {
    document.body.classList.toggle('is-light');
  }
}

interface StockData {
  closing_price: number;
  // Add other properties that you expect in the response
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Finance Dashboard</title>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bulma/0.9.3/css/bulma.min.css">
  <script defer src="https://use.fontawesome.com/releases/v5.14.0/js/all.js"></script>
</head>

<body>
  <section class="hero is-dark is-bold">
    <div class="hero-body">
      <div class="container has-text-centered">
        <h1 class="title">Finance Dashboard</h1>
        <h2 class="subtitle">Monitor key financial metrics</h2>
        <button class="button is-light" (click)="toggleTheme()">Toggle Theme</button>
      </div>
    </div>
  </section>

  <div class="container mt-5">
    <div class="columns">
      <div class="column is-one-quarter">
        <aside class="menu">
          <p class="menu-label has-text-white">General</p>
          <ul class="menu-list">
            <li><a>Dashboard</a></li>
            <li><a>Stocks</a></li>
          </ul>
          <p class="menu-label has-text-white">Administration</p>
          <ul class="menu-list">
            <li><a>Team Settings</a></li>
          </ul>
        </aside>
      </div>
      <div class="column is-three-quarters">
        <div class="columns">
          <div class="column is-two-thirds">
            <div class="box has-background-grey-dark">
              <div *ngIf="stockData" class="notification is-primary">
                <h2 class="subtitle">Stock Price: {{ stockData.closing_price | number:'1.2-2' }}</h2>
              </div>
              <div *ngIf="error" class="notification is-danger">
                <h2 class="subtitle">{{ error }}</h2>
              </div>
              <canvas #stockChart width="800" height="400"></canvas>
            </div>
          </div>
          <div class="column">
            <article class="panel is-danger">
              <p class="panel-heading">
                Market News
              </p>
              <div class="panel-block" *ngFor="let news of marketNews">
                <article class="media">
                  <div class="media-content">
                    <div class="content">
                      <p class="news-title">{{ news.title }}</p>
                      <p class="news-content">{{ news.content }}</p>
                      <p class="news-meta">
                        <span class="news-ticker">Ticker: {{ news.tickers }}</span> |
                        <span class="news-date">Posted: {{ news.posted_on }}</span>
                      </p>
                    </div>
                  </div>
                </article>
              </div>
            </article>
          </div>
        </div>
        <!-- Additional Stats and Graphs -->
        <div class="columns">
          <div class="column is-half">
            <article class="panel is-info">
              <p class="panel-heading">
                Revenue Trends
              </p>
              <canvas #revenueChart width="400" height="200"></canvas>
            </article>
          </div>
          <div class="column is-half">
            <article class="panel is-warning">
              <p class="panel-heading">
                Earnings Insights
              </p>
              <canvas #earningsChart width="400" height="200"></canvas>
            </article>
          </div>
        </div>
      </div>
    </div>
  </div>
</body>

</html>
